import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';
import PropTypes from 'prop-types'

export default class News extends Component {
    static defaultProps = {
        country: 'in',
        pageSize: 8,
        category: 'general'
    }
    static propTypes = {
        country: PropTypes.string,
        pageSize: PropTypes.number,
        category: PropTypes.string
    }
    capitalizeFirstLetter = (string) => {
        return string.charAt(0).toUpperCase() + string.slice(1);
    }
    constructor(props) {
        super(props);

        this.state = {
            articles: [],
            loading: false,
            page: 1,
            totalResults: 0,

        }
        document.title = `${this.capitalizeFirstLetter(this.props.category)}-NewsMonkey`

    }
    updateNews = async () => {
        const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=aaefd60d690a46b686d067ae9ee8f047&page=${this.state.page}&pageSize=${this.props.pageSize}`;
        this.setState({
            loading: true,
        })
        let data = await fetch(url);
        let parsedData = await data.json();

        this.setState({

            articles: parsedData.articles,
            loading: false,
        })


    }

    async componentDidMount() {
        this.updateNews();
    }
    handleNextClick = async () => {
        if (this.state.page + 1 > Math.ceil(this.state.totalResults / this.props.pageSize)) {
            // console.log(this.state.page + 1);
        }
        else {
            // let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=aaefd60d690a46b686d067ae9ee8f047&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
            // this.setState({
            //     loading: true,
            // })
            // let data = await fetch(url);
            // let parsedData = await data.json();

            // //console.log(parsedData);
            // this.setState({
            //     page: this.state.page + 1,
            //     articles: parsedData.articles,
            //     loading: false,

            // })
            this.setState({
                page: this.state.page + 1,
            })
        }
        console.log(this.state.totalResults);
        this.updateNews();

        //console.log(this.state.page);
        //console.log(Math.ceil(this.state.totalResults / this.props.pageSize));
    }
    handlePrevClick = async () => {
        // let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=aaefd60d690a46b686d067ae9ee8f047&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`;
        // this.setState({
        //     loading: true,
        // })
        // let data = await fetch(url);
        // let parsedData = await data.json();

        // this.setState({
        //     page: this.state.page - 1,
        //     articles: parsedData.articles,
        //     loading: false,
        // })
        this.setState({
            page: this.state.page - 1,
        })
        this.updateNews();
        //console.log("previ");
    }
    render() {
        return (
            <div className='container my-3'>
                <h2 className='text-center'>NewsMonkey-top headlines on {this.capitalizeFirstLetter(this.props.category)} Category</h2>
                {this.state.loading && <Spinner />}
                <div className='row'>
                    {!this.state.loading && this.state.articles.map((element) => {


                        return <div className='col-md-3' key={element.url}>
                            <NewsItem title={element.title ? element.title.slice(0, 45) : ""} description={element.description ? element.description.slice(0, 80) : ""} imageurl={element.urlToImage} newsurl={element.url} author={element.author} date={element.publishedAt} />
                        </div>






                    })}





                </div>
                <div className="container d-flex justify-content-between">
                    <button type="button" className="btn btn-dark" disabled={this.state.page <= 1} onClick={this.handlePrevClick}> &larr;Previous</button>
                    <button type="button" className="btn btn-dark" onClick={this.handleNextClick} disabled={this.state.page >= Math.ceil(this.state.totalResults / this.props.pageSize)}> Next &rarr;</button>
                </div >

            </div>
        )
    }
}
